import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faHome, faSignInAlt, faSignOutAlt, faUserMd } from '@fortawesome/free-solid-svg-icons'
import './Navbar.css'
import {Link} from 'react-router-dom'
import { useState } from 'react'
import Modal from 'react-modal';
import LoginModal from '../Modals/LoginModal'
import SignupModal from '../Modals/SignupModal' 
import LogoutModal from '../Modals/LogoutModal'
import { Authentication } from '../../Contexts/Authentication';

const Navbar = (props) => {
    const [isLoginOpen,setIsLoginOpen] = useState(false);
    const [isSignupOpen,setIsSignupOpen] = useState(false);
    const [isLogoutOpen,setIsLogoutOpen] = useState(false);
    const {isLogged, setIsLogged} = useContext(Authentication)

    const openLogin = () => {
        setIsLoginOpen(true)
    }
    const closeLogin = () => {
        setIsLoginOpen(false)
    }
    const openSignup = () => {
        setIsSignupOpen(true)
    }
    const closeSignup = () => {
        setIsSignupOpen(false)
    }
    const handleLogout = () => {
        setIsLogoutOpen(true)
    }

    const modalStyle = {
        overlay: {
            backgroundColor     : '#2b1d13a0'
        },
        content : {
          borderRadius          : '13px',
          border                : '1px solid black',
          backgroundColor       : '#997b66',
          top                   : '50%',
          left                  : '50%',
          right                 : 'auto',
          bottom                : 'auto',
          marginRight           : '-50%',
          transform             : 'translate(-50%, -50%)',
          display               : 'flex',
          flexDirection : 'column',
          justifyContent : 'center'
        }
      };

    return (
        <div className="nav-container">
            <ul className="nav-links-container">
                <Link to="/"> 
                    <li className="nav-links"><FontAwesomeIcon color='#ffc55a' icon={faHome} size="2x"/></li>
                </Link>
                {!props.isLogged && <li className="nav-links" onClick={openLogin}><FontAwesomeIcon color='#ffc55a' icon={faSignInAlt} size="2x"/></li>}
                {props.isLogged && <Link to='/'> 
                    <li className="nav-links"><FontAwesomeIcon color='#ffc55a' icon={faUserMd} size="2x"/></li>
                </Link>}
                {props.isLogged && 
                    <li className="nav-links"><FontAwesomeIcon color='#ffc55a' onClick={handleLogout}icon={faSignOutAlt} size="2x"/></li>
                }
            </ul>

            <Modal
          isOpen={isLoginOpen}
          onRequestClose={closeLogin}
          style={modalStyle}
          contentLabel="Example Modal"
          closeLogin={closeLogin}
            >
                <LoginModal setIsLoginOpen={setIsLoginOpen} setIsSignupOpen={setIsSignupOpen}/>
            </Modal>
            <Modal
          isOpen={isSignupOpen}
          onRequestClose={closeSignup}
          style={modalStyle}
          contentLabel="Example Modal"
          closeLogin={closeSignup}
            >
                <SignupModal setIsLoginOpen={setIsLoginOpen} setIsSignupOpen={setIsSignupOpen}/>
            </Modal>
            <Modal
          isOpen={isLogoutOpen}
          onRequestClose={() => setIsLogoutOpen(false)}
          style={modalStyle}
          contentLabel="Example Modal"
          closeLogin={() => setIsLogoutOpen(false)}
            >
                <LogoutModal setIsLogoutOpen={setIsLogoutOpen}/>
            </Modal>

        </div>
    )
}

export default Navbar
